import { useRef, useState, useEffect } from "react";

const Login = () => {   
    const userRef = useRef();
    const errorRef = useRef();

    const [username, setUser] = useState('');
    const [password, setPassword] = useState('');
    const [userError, setError] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setError('')
    }, [username, password]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        axios.post("http://localhost:8080/auth/login", loginPayload).then(response => {
            const token  =  response.data;
            localStorage.setItem("token", `Bearer_${token}`);
            /*axios.interceptors.request.use(
                config => {
                    config.headers.authorization = `Bearer_${token}`
                    return config;
                },
                error => {
                    return Promise.reject(error);
                }
            )*/
        })
    }

    return (
        <div>
            <p ref={errorRef} className={userError ? "error" : "none"} aria-live="assertive">
                {userError}
            </p> 

            <h1>
                Login
            </h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username:</label>
                <input 
                    type="text"
                    id="username"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setUser(e.target.value)}
                    value={username}
                    required
                />

                <label htmlFor="password">Password:</label>
                <input 
                    type="password"
                    id="password"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    required
                />

                <button>Sign In</button>
            </form>

            <p>
                Create new account<br/>
                <span className="line">
                    <a href="register">Register</a>
                </span>
            </p>
        </div>
    )
}

export default Login;
