import React from 'react';
import { useRef, useState, useEffect } from "react";
import './../../style/common-styles.css';
import { Button } from 'react-bootstrap';
import axios from 'axios';
import { useParams } from "react-router-dom";

const Subscribe = (props) => {
    const [isSubscribed, setIsSubscribed] = useState(false)
    const [buttonName, setButtonName] = useState('Unsubscribe');

    let { id } = useParams()

    useEffect(() => {
        axios.get("http://localhost:8100/profile/" + id + "/is-subscriber").then(res => {
            setIsSubscribed(res.data);
        })
        console.log("isSub - " + isSubscribed)
        if(isSubscribed) {
            setButtonName('Unsubscribe');
        } else {
            setButtonName('Subscribe');
        }
        console.log(buttonName)
    }, [])

    useEffect(() => {
        if(isSubscribed) {
            setButtonName('Unsubscribe');
        } else {
            setButtonName('Subscribe');
        }
    }, [isSubscribed]);

    const onClick = () => {
        let subscribeUrl = 'http://localhost:8100/profile/' + id;
        if(isSubscribed) {
            axios.delete(subscribeUrl).then(res => {
                setIsSubscribed(false);
            })
        } else {
            axios.post(subscribeUrl).then(res => {
                setIsSubscribed(true);
            })
        }
    }

    return (
        <div>
            {buttonName}
            <Button onClick={onClick}>{buttonName}</Button>
        </div>
    )
}

export default Subscribe;
