import { useRef, useState, useEffect } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import './auth-styles.css'

const Registration = () => {
    const userRef = useRef();
    const errorRef = useRef();

    const [username, setUser] = useState('');
    const [password, setPassword] = useState('');
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [gender, setGender] = useState('')

    const [userError, setError] = useState('');
    const [success, setSuccess] = useState(false);

    const navigate = useNavigate();

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setError('')
    }, [username, password, repeatedPassword, firstName, lastName, gender]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        let loginPayload = {
            email: username,//'test1@gmail.com',
            password: password//'123456789'
        }

        axios.post("http://localhost:8080/auth/registration", loginPayload).then(response => {
            const token = response.data;
            localStorage.setItem("token", `Bearer_${token}`);
            if (response.status == 200) {
                setSuccess(true)
                navigate("/")
            }
        })

        if (!success) {
            console.log("error")
            setError("Invalid credentials, try again");
            console.log(userError);
        }
    }

    return (
        <div class="flex-center bg-black full-screen">
            <div class="padding-left-45 login-form-wrapper">
                <div class="login-form-wrapper">
                    <p ref={errorRef} className={userError ? "error-msg" : "none"} aria-live="assertive">
                        {userError}
                    </p>

                    <h1>
                        Register
                    </h1>
                    <form onSubmit={handleSubmit}>
                        <div className="registration-form">
                            <label htmlFor="username">Email:</label>
                            <input
                                type="text"
                                id="username"
                                ref={userRef}
                                autoComplete="off"
                                onChange={(e) => setUser(e.target.value)}
                                value={username}
                                required
                            />

                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />

                            <label htmlFor="password"> Repeated Password:</label>
                            <input
                                type="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />

                            <label htmlFor="password">First Name:</label>
                            <input
                                type="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />

                            <label htmlFor="password">Last Name:</label>
                            <input
                                type="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />

                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />

                            <label htmlFor="password">Gender:</label>
                            <input
                                type="password"
                                id="password"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />

                            <button>Register</button>
                        </div>
                    </form>


                    <p>
                        Sign into existing account:
                        <span className="line">
                            <a href="/"> Login </a>
                        </span>
                    </p>
                </div>
            </div>
        </div>
    )
}

export default Registration;
