    const [buttonAction, setButtonAction] = useState({
        name: 'Subscribe',
        isSubscribed: false
    });
    const [isSubscribed, setIsSubscribed] = useState(false)

    useEffect(() => {
        let subscribeButton = isSubscribed? {
            name: 'Subscribe',
            isSubscribed: false
        } : {
            name: 'Unsubscribe',
            isSubscribed: true
        }
        setButtonAction(subscribeButton);
    }, [])

    const onClick = () => {
        
    }

    return (
        <div>
            <Button onClick={onClick}>{buttonAction.name}</Button>
        </div>
    )
}

export default Subscribe;
