import { useRef, useState, useEffect } from "react";
import React, { Component } from 'react';
import axios from 'axios';
import Modal from 'react-modal'
import { Button } from 'react-bootstrap';
import './../common/style/common-styles.css'
import './../common/style/common-element-styles.css'
import Posts from './../common/component/Posts'

const Profile = () => {
    axios.interceptors.request.use(
        config => {
            config.headers.authorization = localStorage.getItem('token');
            return config;
        },
        error => {
            return Promise.reject(error);
        }
    )

    const [isOpen, setIsOpen] = useState(false);
    const [postText, setPostText] = useState('');
    const [userPosts, setUserPosts] = useState([]);
    const [userData, setUserData] = useState({
        posts: [],
        username: ''
    });

    function toggleModal() {
      setIsOpen(!isOpen);
    }

    const createPost = () => {
        const postData = {
            text: postText
        }
        axios.post("http://localhost:8080/profile/posts", postData).then(response => {
            console.log(response)
            setUserPosts(response.data);
        })
        toggleModal();
    }

    const fetchCurrentUserProfile = () => {
        axios.get("http://localhost:8080/profile").then(res => {
            setUserData(res.data);
            setUserPosts(res.data.posts)
        })
    }

    useEffect(() => {
        fetchCurrentUserProfile();
    }, [])

    return (
        <div>
            <h1>{userData}</h1>
            <Posts posts={userPosts}/>
            <Button onClick={toggleModal}>Create Post</Button>
            <Modal 
                className="modal"
                isOpen={isOpen}
                onClose={toggleModal}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <div class="flex-start flex-column">
                    <h1 class="margin-bot-70">Create Post</h1>
                    <textarea class="width-600 height-100 margin-bot-40"
                        type="textarea"
                        id="setPostText"
                        autoComplete="off"
                        onChange={(e) => setPostText(e.target.value)}
                        value={postText}
                        required
                    />
                    <div class="flex-space-between width-590 padding-left-10 margin-bot-20">
                        <Button className="blue-button" onClick={createPost}>Add Post</Button>
                        <Button className="blue-button" onClick={toggleModal}>Cancel</Button>
                    </div>
                </div>
            </Modal>
        </div>

    )
}

export default Profile;
