import { useRef, useState, useEffect } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";

const Login = () => {   
    const userRef = useRef();
    const errorRef = useRef();

    const [username, setUser] = useState('');
    const [password, setPassword] = useState('');
    const [userError, setError] = useState('');
    const [success, setSuccess] = useState(false);
    
    const navigate = useNavigate();

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setError('')
    }, [username, password]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        let loginPayload = {
            email: username,//'test1@gmail.com',
            password: password//'123456789'
        }

        axios.post("http://localhost:8080/auth/login", loginPayload).then(response => {
            const token  =  response.data;
            localStorage.setItem("token", `Bearer_${token}`);
            if(response.status == 200) {
                success = true;
                navigate("/profile")
            }
        })
        
        if(!success) {
            console.log("error")
            setError("Invalid credentials, try again");
            console.log(userError);
        }
    }

    return (
        <div>
            <p ref={errorRef} className={userError ? "error" : "none"} aria-live="assertive">
                {userError}
            </p> 

            <h1>
                Login
            </h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username:</label>
                <input 
                    type="text"
                    id="username"
                    ref={userRef}
                    autoComplete="off"
                    onChange={(e) => setUser(e.target.value)}
                    value={username}
                    required
                />

                <label htmlFor="password">Password:</label>
                <input 
                    type="password"
                    id="password"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    required
                />

                <button>Sign In</button>
            </form>

            <p>
                Create new account<br/>
                <span className="line">
                    <a href="register">Register</a>
                </span>
            </p>
        </div>
    )
}

export default Login;
