import React from 'react';
import { useRef, useState, useEffect } from "react";
import InstrumentsList from './InstrumentsEnums';
import BsFillStarFill from 'react-icons/bs';
import { FaGuitar, FaDrum, FaMicrophoneAlt } from "react-icons/fa";
import './../../style/common-styles.css';

const Instrument = (props) => {
    const [instrumentIcon, setInstrumentIcon] = useState('');
    const [instrument, setInstrument] = useState({});
    const [starsAmount, setStarsAmount] = useState([]);

    const defineInstrument = () => {
        setInstrument(props.instrument);
    }

    const defineInstrumentIcon = () => {
        setInstrumentIcon(InstrumentsList[instrument]);
    }

    const defineStarsAmount = () => {
        console.log(props.instrument);
        let stars = Array.from(Array(props.instrument.skillLevel).keys())
        setStarsAmount(stars);
    }

    useEffect(() => {
        defineInstrument();
        defineInstrumentIcon();
        defineStarsAmount();
    }, [])

    return (
        <div class="instrument">
            <div class="not-displayed">ID: {instrument.id}</div>
            <div class="flex-start">
                <div>icon: {instrumentIcon}</div>
                <div>{instrument.instrumentName} </div>
                <FaGuitar />
                <FaDrum />
                <FaMicrophoneAlt />
                <div>{
                    starsAmount.map(i =>
                        <BsFillStarFill />
                    )
                }
                </div>
            </div>
        </div>
    )
}

export default Instrument;
