import React from 'react';

export default class Post extends React.Component {
    render() {
        return (
            <div class="width-600 flex-start flex-column">
                <div class="not-displayed">ID: {this.props.id}</div>
                <div>Content: {this.props.text}</div>
                <div class="flex-start">
                    <div>Date: {
                        new Date(this.props.creationDateTime).getDate() + ' ' +
                        new Date(this.props.creationDateTime).getMonth() + ' ' +
                        new Date(this.props.creationDateTime).getFullYear()
                    }</div>
                    <div>Time: {
                        new Date(this.props.creationDateTime).getHours() + ' ' +
                        new Date(this.props.creationDateTime).getMinutes()
                    }</div>
                    <div class="margin-left-20">Likes: {this.props.likes}</div>
                </div>
            </div>
        )
    }
}
