import './App.css';
import React, {useState, useEffect} from 'react'
import axios from 'axios';

const HandleLogin = (email, pass) => {
  const loginPayload = {
    email: 'test1@gmail.com',
    password: '123456789'
  }

  /*axios.interceptors.request.use(
    config => {
      config.headers.authorization = `Bearer_eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ0ZXN0MUBnbWFpbC5jb20iLCJyb2xlcyI6WyJVU0VSIl0sImlhdCI6MTY1NzUwNTA1OCwiZXhwIjoxNjU3NTQxMDU4fQ.u6t2MGBJhhwfzYUNsQlfKzfscL3D7dzsH7PWZcCGHvc`
      return config;
    },
    error => {
      return Promise.reject(error);
    }
  )*/

  axios.post("http://localhost:8080/auth/login", loginPayload)
    .then(response => {
      const token  =  response.data;
      localStorage.setItem("token", `Bearer_${token}`);
      axios.interceptors.request.use(
        config => {
          config.headers.authorization = `Bearer_${token}`
          return config;
        },
        error => {
          return Promise.reject(error);
        }
      )
    })
};

const UserProfile = () => {
  const fetchCurrentUserProfile = () => {
    axios.headers.authorization = localStorage.getItem('token');
    axios.get("http://localhost:8080/profile").then(res => {
      console.log(res);
    })
  }

  useEffect(() => {
    fetchCurrentUserProfile();
  }, [])

  return  <h1>Hello</h1>
}

function App() {
  return (
    <div className="App">
      <HandleLogin />
      <UserProfile />
    </div>
  );
}

export default App;
