import React from 'react';
import { Link } from 'react-router-dom';

export default class Post extends React.Component {
    render() {
        return (
            <div class="post padding-15">
                <Link to={'/profile/' + this.props.post.ownerId}>
                    {this.props.post.ownerFullName}
                </Link>
                <div>Content: {this.props.post.text}</div>
                <div class="flex-start">
                    <div>Date: {
                        new Date(this.props.post.creationDateTime).getDate() + '/' +
                        new Date(this.props.post.creationDateTime).getMonth() + '/' +
                        new Date(this.props.post.creationDateTime).getFullYear()
                    }</div>
                    <div>-{
                        new Date(this.props.post.creationDateTime).getHours() + ':' +
                        new Date(this.props.post.creationDateTime).getMinutes()
                    }</div>
                    <div class="margin-left-20">Likes: {this.props.post.likes}</div>
                </div>
            </div>
        )
    }
}
