import { useRef, useState, useEffect } from "react";
import React, { Component } from 'react';
import axios from 'axios';
import Modal from 'react-modal'
import { Button } from 'react-bootstrap';
import './../common/style/common-styles.css'
import './../common/style/common-element-styles.css'
import Posts from '../common/entity/Posts'
import SideBar from '../common/navigation/SideBar'
import Instrument from '../common/entity/instrument/Instrument'

const Profile = () => {
    axios.interceptors.request.use(
        config => {
            config.headers.authorization = localStorage.getItem('token');
            return config;
        },
        error => {
            return Promise.reject(error);
        }
    )

    const [isPostModalOpen, setPostModalOpen] = useState(false);
    const [isUpdateProfileModalOpen, setUpdateProfileModalOpen] = useState(false);
    const [postText, setPostText] = useState('');
    const [userPosts, setUserPosts] = useState([]);
    const [userData, setUserData] = useState({});
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [status, setStatus] = useState('');

    function togglePostModal() {
        setPostModalOpen(!isPostModalOpen);
    }

    function toggleProfileModal() {
        setUpdateProfileModalOpen(!isUpdateProfileModalOpen);
    }

    const createPost = () => {
        const postData = {
            text: postText
        }
        axios.post("http://localhost:8080/profile/posts", postData).then(response => {
            setUserPosts(response.data);
        })
        togglePostModal();
    }

    const updateProfile = () => {
        const profileData = {
            firstName: firstName,
            lastName: lastName,
            status: status
        }
        axios.put("http://localhost:8080/profile", profileData).then(response => {
            fetchCurrentUserProfile();
        })
        toggleProfileModal();
    }

    const fetchCurrentUserProfile = () => {
        axios.get("http://localhost:8080/profile").then(res => {
            setUserData(res.data);
            setUserPosts(res.data.posts)
            setFirstName(res.data.firstName)
            setLastName(res.data.lastName)
            setStatus(res.data.status);
        })
    }

    useEffect(() => {
        fetchCurrentUserProfile();
    }, [])

    return (
        <div>
            <SideBar />
            <div class="margin-left-350">
                <div class="username">{firstName} {lastName}</div>
                <div class="flex-space-between width-630">
                    <div class="padding-right-30">
                        {status}
                    </div>
                    <div class="max-height-65"> {[
                        {instrumentName: 'GUITAR', skillLevel: 3, id: 1},
                        {instrumentName: 'VOCAL', skillLevel: 4, id: 2},
                        {instrumentName: 'DRUMS', skillLevel: 2, id: 3}
                    ].map((instrument) => {
                        return <div>
                        <Instrument 
                            instrument={instrument} 
                        />
                        </div>
                    })}
                    </div>                    
                </div>
                <Button className="margin-top-25" onClick={toggleProfileModal}>Update Info</Button>
                <Modal 
                    className="modal"
                    isOpen={isUpdateProfileModalOpen}
                    onClose={toggleProfileModal}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <div class="flex-start flex-column">
                        <h1>Profile</h1>
                        <label class="padding-5" htmlFor="firstName">First Name:</label>
                        <input
                            type="text"
                            id="firstName"
                            onChange={(e) => setFirstName(e.target.value)}
                            value={firstName}
                            required
                            class="padding-5"
                        />
                        <label class="padding-5" htmlFor="lastName">Last Name:</label>
                        <input
                            type="text"
                            id="lastName"
                            onChange={(e) => setLastName(e.target.value)}
                            value={lastName}
                            required
                            class="padding-5"
                        />
                        <label class="padding-5" htmlFor="status">Status:</label>
                        <textarea class="width-600 height-100 margin-bot-40 padding-5"
                            placeholder="My status..."
                            type="textarea"
                            id="status"
                            autoComplete="off"
                            onChange={(e) => setStatus(e.target.value)}
                            value={status}
                            required
                        />
                        <div class="flex-space-between width-590 padding-left-10 margin-bot-20">
                            <Button className="blue-button" onClick={updateProfile}>Update</Button>
                            <Button className="blue-button" onClick={toggleProfileModal}>Cancel</Button>
                        </div>
                    </div>
                </Modal>
                <Posts posts={userPosts}/>
                <Button onClick={togglePostModal}>Create Post</Button>
                <Modal 
                    className="modal"
                    isOpen={isPostModalOpen}
                    onClose={togglePostModal}
                    aria-labelledby="modal-modal-title"
                    aria-describedby="modal-modal-description"
                >
                    <div class="flex-start flex-column">
                        <h1 class="margin-bot-70">Create Post</h1>
                        <textarea class="width-600 height-100 margin-bot-40"
                            type="textarea"
                            id="setPostText"
                            autoComplete="off"
                            onChange={(e) => setPostText(e.target.value)}
                            value={postText}
                            required
                        />
                        <div class="flex-space-between width-590 padding-left-10 margin-bot-20">
                            <Button className="blue-button" onClick={createPost}>Add Post</Button>
                            <Button className="blue-button" onClick={togglePostModal}>Cancel</Button>
                        </div>
                    </div>
                </Modal>
            </div>
        </div>

    )
}

export default Profile;
