import React from 'react';
import { useRef, useState, useEffect } from "react";
import Instrument from './Instrument.js'
import './../../style/common-styles.css';
import { Button } from 'react-bootstrap';
import axios from 'axios';

const InstrumentsListPlaceholder = (props) => {
    const [instruments, setInstruments] = useState([]);
    const [updatedInstrumentData, setUpdatedInstrumentData] = useState({
        instrumentName: "GUITAR",
        skillLevel: 1,
        genres: ["ROCK", "BLUES", "JAZZ"]
    });

    useEffect(() => {
        setInstruments(props.instruments);
    }, [])

    const onAddInstrument = () => {
        axios.post("http://localhost:8100/profile/instruments", updatedInstrumentData).then(res => {
            //setInstruments(res.data);
            props.handleUpdate(res.data);
        })
        //let instrumentsWithEmptyRow = instruments;
        //instrumentsWithEmptyRow.push({
        //    id: -1,
        //    instrumentName: "GUITAR",
        //    skillLevel: 1
        //});
        //setInstruments(instrumentsWithEmptyRow);
    }

    return (
        <div>
            {props.isEditingEnabled && <Button onClick={onAddInstrument}>Add instrument</Button>}
            <div class="max-height-65"> {props.instruments.map((instrument) => {
                return <div>
                <Instrument 
                    instrument={instrument} 
                />
                </div>
            })}
            </div>  
        </div>
    )
}

export default InstrumentsListPlaceholder;
