import './App.css';
import React, {useState, useEffect} from 'react'
import axios from 'axios';

const appUrl = "http://localhost:8080/"

const authAxios = axios.create({
  headers: {
  }
})

const HandleLogin = (email, pass) => {
  const loginPayload = {
    email: 'test1@gmail.com',
    password: '123456789'
  }

  axios.post(appUrl + "auth/login", loginPayload)
    .then(response => {
      const token  =  response.data;
      authAxios.headers["Authorization"] = "Bearer_${token}"
      axios.defaults.headers.common["Authorization"] = `Bearer_${token}`;
    })
    .catch(err => console.log(err));
};

const UserProfile = () => {
  const fetchCurrentUserProfile = () => {
    console.log(axios.defaults.headers);
    authAxios.get(appUrl + "profile").then(res => {
      console.log(res);
    })
  }

  useEffect(() => {
    fetchCurrentUserProfile();
  }, [])

  return  <h1>Hello</h1>
}

function App() {
  return (
    <div className="App">
      <HandleLogin />
      <UserProfile />
    </div>
  );
}

export default App;
