import React from 'react';
import { useRef, useState, useEffect } from "react";
import Instrument from './Instrument.js'
import './../../style/common-styles.css';
import { Button } from 'react-bootstrap';

const InstrumentsListPlaceholder = (props) => {
    const [instruments, setInstruments] = useState([]);

    useEffect(() => {
        setInstruments(props.instruments);
    }, [])

    const onAddInstrument = () => {
        let instrumentsWithEmptyRow = instruments;
        instrumentsWithEmptyRow.push({
            id: -1,
            instrumentName: "GUITAR",
            skillLevel: 1
        });
        setInstruments(instrumentsWithEmptyRow);
    }

    return (
        <div>
            <Button onClick={onAddInstrument}>Add instrument</Button>
            <div class="max-height-65"> {instruments.map((instrument) => {
                return <div>
                <Instrument 
                    instrument={instrument} 
                />
                </div>
            })}
            </div>  
        </div>
    )
}

export default InstrumentsListPlaceholder;
